job.command:
starver SL15c

setenv PRODPATH &basePath;/production/&prodId;
setenv LOGPATH  &basePath;/jobs/log/&prodId;

mkdir -p ${PRODPATH} ${LOGPATH}

if ("&mMode;" == "1") then
    &basePath;/jobs/starSubmit/parseFileList.csh ${FILELIST} &basePath; &prodId; &mMode; &treeName; ${JOBID} &rootMacro; picoList_badRuns.list
else
    root -q -b -l StRoot/macros/loadSharedHFLibraries.C StRoot/macros/&rootMacro;++\(\"$FILELIST\",\"&prodId;_$JOBINDEX\",&mMode;,\"picoList_badRuns.list\"\) >& ${JOBID}.log
    tar -zcvf ${JOBID}.log.tgz ${JOBID}.log
endif
